var https = require('https');
var url = require('url');
var AWS = require('aws-sdk');
function send(event, context, status, payload) {
    var body = JSON.stringify({
        StackId: event.StackId,
        RequestId: event.RequestId,
        LogicalResourceId: event.LogicalResourceId,
        NoEcho: false,
        Data: payload,
        Status: status,
        Reason: 'See the details in CloudWatch Log Stream: ' + context.logStreamName,
        PhysicalResourceId: context.logStreamName
    });
    var requestOptions = {
        headers: {
            'content-length': body.length,
            'content-type': ''
        },
        hostname: url.parse(event.ResponseURL).hostname,
        port: 443,
        path: url.parse(event.ResponseURL).path,
        method: 'PUT'
    };
    var makeRequest = https.request(requestOptions, function(response) {
        context.done();
    });
    makeRequest.on('error', function(error) {
        context.done();
    });
    makeRequest.write(body);
    makeRequest.end();
}
var s3 = new AWS.S3();
exports.handler = function(event, context) {
  var respond = (e) => response.send(event, context, e ? response.FAILED : response.SUCCESS, e ? e : {});
  var params = event.ResourceProperties;
  delete params.ServiceToken;
  if (event.RequestType === 'Delete') {
    params.NotificationConfiguration = {};
    s3.putBucketNotificationConfiguration(params).promise()
      .then((data)=>respond())
      .catch((e)=>respond());
  } else {
    s3.putBucketNotificationConfiguration(params).promise()
      .then((data)=>respond())
      .catch((e)=>respond(e));
  }
};
